INSERT INTO `wp_postmeta` VALUES("56", "12", "_panels_data_preview", "a:3:{s:7:\"widgets\";a:2:{i:0;a:2:{s:5:\"title\";s:16:\"PERSONAL DETAILS\";s:11:\"panels_info\";a:6:{s:5:\"class\";s:18:\"pulse_title_widget\";s:3:\"raw\";b:0;s:4:\"grid\";i:0;s:4:\"cell\";i:0;s:2:\"id\";i:0;s:5:\"style\";a:1:{s:18:\"background_display\";s:4:\"tile\";}}}i:1;a:2:{s:6:\"mytabs\";a:4:{i:0;a:4:{s:8:\"tabtitle\";s:3:\"BIO\";s:8:\"subtitle\";s:8:\"ABOUT ME\";s:7:\"content\";s:409:\"<p>I\'m <strong>Isaac</strong>, a Front End Developer and Designer who enjoys the thrills of learning, who marvels in the ways of creativity, and who stands firm in the failures of everyday life. I strive to be a developer with high standards in quality web-design and humility.</p>\n\n<p>When not solving syntax errors, you\'ll find me watching Netflix with my wife, gaming, or getting hugs from my daughter.</p>\";s:9:\"faicontab\";s:7:\"fa-user\";}i:1;a:4:{s:8:\"tabtitle\";s:7:\"HOBBIES\";s:8:\"subtitle\";s:9:\"INTERESTS\";s:7:\"content\";s:669:\"<p>GAMING: Love playing anything made by Blizzard, Bethesda, and/or Bungie. The three B\'s! Lover of all the Zelda games, too.</p>\n\n<p>MUSIC: I play guitar, piano, and sing! (Didn\'t say I was good at them) Music has always been a great medium for me to express myself and is an art that demands both disciple and beauty.</p>\n\n<p>SPORTS: I\'m a sports watcher and really enjoy staying active through; biking, jogging, softball, golf, and weightlifting (Yes bro, I do lift).</p>\n\n<p>READING: Love reading fantasy novels (e.g. Lord of the Rings, Hobbit, Chronicles of Narnia, King Killer Chronicles). I also enjoy reading the Bible and books based on Biblical teachings.</p>\";s:9:\"faicontab\";s:6:\"fa-pad\";}i:2;a:4:{s:8:\"tabtitle\";s:6:\"CAUSES\";s:8:\"subtitle\";s:19:\"CAUSES I CARE ABOUT\";s:7:\"content\";s:423:\"<p>POVERTY: One billion people on this planet are currently malnutritious.</p>\n<p>YOUTH: As a teenager, I lost a mentor and friend; my older brother. I now desire to help teenagers love their uniqueness and love others through volunteering and mentoring.</p>\n<p>LIFE: I believe all human beings are valuable, from conception unto eternity.</p>\n<p>EDUCATION: Knowledge is a tool that each individual should have access. </p>\";s:9:\"faicontab\";s:8:\"fa-heart\";}i:3;a:4:{s:8:\"tabtitle\";s:9:\"VOLUNTEER\";s:8:\"subtitle\";s:14:\"VOLUNTEER WORK\";s:7:\"content\";s:521:\"<p>FRONT END WEB INSTRUCTOR: Worked as an instructor teaching students, ages 11-18, the basic foundations of web design. The curriculum began introducing the students to HTML and CSS, and ending with a final product web page of their own design. </p>\n<p>YOUTH MENTOR: Worked with students, ages 11-18, as an assistant instructor of biblical studies. Mentored students to believe in themselves, identify their personal stories,  remain true to their beliefs, love unconditionally, and find value in selfless behavior. </p>\";s:9:\"faicontab\";s:8:\"fa-users\";}}s:11:\"panels_info\";a:5:{s:5:\"class\";s:9:\"side_tabs\";s:4:\"grid\";i:0;s:4:\"cell\";i:0;s:2:\"id\";i:1;s:5:\"style\";a:2:{s:27:\"background_image_attachment\";b:0;s:18:\"background_display\";s:4:\"tile\";}}}}s:5:\"grids\";a:1:{i:0;a:2:{s:5:\"cells\";i:1;s:5:\"style\";a:0:{}}}s:10:\"grid_cells\";a:1:{i:0;a:2:{s:4:\"grid\";i:0;s:6:\"weight\";i:1;}}}");
